def download_all_images(html, base, directory):
    """
    Parses HTML to find and download all images.
    """
    # Create the download directory if it doesn't exist
    if not os.path.exists(directory):
        os.makedirs(directory)
        print(f"Created directory: {directory}")

    # Parse the HTML content
    soup = BeautifulSoup(html, 'html.parser')
    
    # Find all image tags
    img_tags = soup.find_all('img')
    
    if not img_tags:
        print("No <img> tags were found in the provided HTML.")
        return

    print(f"Found {len(img_tags)} image(s). Starting download...")

    # Loop through all found image tags
    for img_tag in img_tags:
        img_url = img_tag.get('src')
        if not img_url:
            continue # Skip tags that have no 'src' attribute

        # Convert relative URL to an absolute URL
        # e.g., turn '/images/cat.jpg' into 'https://example.com/images/cat.jpg'
        img_url = urljoin(base, img_url)

        try:
            # Get the image content
            response = requests.get(img_url, stream=True)
            # Raise an error for bad responses (4xx or 5xx)
            response.raise_for_status()

            # Extract a clean filename from the URL
            filename = os.path.basename(img_url.split("?")[0])
            filepath = os.path.join(directory, filename)

            # Save the image to the local file
            with open(filepath, 'wb') as f:
                for chunk in response.iter_content(8192):
                    f.write(chunk)
            
            print(f"✅ Successfully downloaded: {filename}")

        except requests.exceptions.RequestException as e:
            print(f"❌ Failed to download {img_url}. Error: {e}")
        except Exception as e:
            print(f"❌ An error occurred for URL {img_url}: {e}")

    print("\nDownload process finished.")


# --- Run the script ---
